import "../../App.css";
// reactstrap components
import * as React from "react";
import axios from "axios";

let films = [];
let filmActuel = [];

class Widget6 extends React.Component {
  fetch = async () => {
    try {
      const { data: film } = await axios.get("http://localhost:5000");
      films = film;
      let pop = 0;
      for (let i = 0; i < films.length; i++) {
        if (parseInt(films[i].popularite) > pop)
          pop = parseInt(films[i].popularite);
      }
      filmActuel = films.find((unFilm) => unFilm.popularite === pop.toString());
    } catch (err) {
      console.error(err);
    }
  };

  render() {
    this.fetch();

    return (
      <div className="simplecard">
        <br />
        Film plus populaire
        <br /> <br />
        <div>{filmActuel && filmActuel.length != 0 && filmActuel.titre}</div>

      </div>
    );
  }
}

export default Widget6;
