import "../../App.css";
// reactstrap components
import * as React from "react";
import axios from "axios";


let films = [];
let filmActuel = [];

class Widget6 extends React.Component {

  fetch = async () => {
    try {
      const { data: film } = await axios.get("http://localhost:5000");
      films = film;
    } catch (err) {
      console.error(err);
    }
  };

  render() {

    this.fetch();

    return (
      <div className="card">
        <br />
        Film plus populaire
        <br /> <br />
        <div>{film && film.length != 0 && film.titre}</div>
        <div>{film && film.length != 0 && film.description}</div>
        <div>{film && film.length != 0 && film.date}</div>
        <div>{film && film.length != 0 && film.popularite}</div>
        <div>{film && film.length != 0 && film.origine}</div>
      </div>
    );
  }
}

export default Widget6;
