import "./App.css";
// reactstrap components
import Grid from "@mui/material/Grid";
import * as React from "react";
import Box from "@mui/material/Box";
import Drawer from "@mui/material/Drawer";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import { Bar } from "react-chartjs-2";
import Chart from "chart.js/auto";

const drawerWidth = 50;

const data = {
  labels: ["Red", "Blue", "Yellow", "Green", "Purple", "Orange"],
  datasets: [
    {
      label: "couleur pref en france",
      data: [43, 19, 3, 5, 2, 3],
    },
  ],
};

class App extends React.Component {
  state = {
    loading: true,
    info: null,
  };

  async componentDidMount() {
    const url =
      "https://data.culture.gouv.fr//api/records/1.0/search/?dataset=frequentation-dans-les-salles-de-cinema&q=&facet=annee&refine.annee=2020";
      const response = await fetch(url);
    const data = await response.json();
    this.setState({ info: data.records[0], loading: false });
      const url2 =
      "https://data.culture.gouv.fr//api/records/1.0/search/?dataset=frequentation-dans-les-salles-de-cinema&q=&facet=annee&refine.annee=2019";
    const response2 = await fetch(url2);
    const data2 = await response2.json();
    this.setState({ info: data2.records[0], loading: false });
  }
  param() {
    data.datasets[0].data[0] = parseInt(
      this.state.info.fields.entrees_millions
    );
    data.labels[0] = this.state.info.fields.annee;

    data2.datasets[0].data[0] = parseInt(
      this.state.info.fields.entrees_millions
    );
    data2.labels[0] = this.state.info.fields.annee;
  }

  render() {
    if (this.state.loading) {
      return <div>loading...</div>;
    }

    if (!this.state.info) {
      return <div>ERREUR</div>;
    }
    this.param();

    return (
      <Box className="body">
        <Drawer sx={{ width: drawerWidth }} variant="permanent" anchor="left">
          <List className="drawer">
            {["HOME", "SEARCH"].map((text, index) => (
              <ListItem button key={text}>
                <ListItemText primary={text} />
              </ListItem>
            ))}
          </List>
        </Drawer>
        <Box component="main" sx={{ flexGrow: 1, bgcolor: "#212427", p: 3 }}>
          <Grid container spacing={1} className="firstcard">
            <Grid item xs={4}>
              <div className="simplecard">
                Nombre d'entrées en millions
                <div>{this.state.info.fields.entrees_millions}</div>
                en <div>{this.state.info.fields.annee}</div>
              </div>
            </Grid>
            <Grid item xs={4}>
              <div className="simplecard">
                Nombre de séances
                <div>{this.state.info.fields.seances_milliers}</div>
              </div>
            </Grid>
            <Grid item xs={4}>
              <div className="simplecard">
                Recette moyenne par personne
                <div>
                  {this.state.info.fields.recette_moyenne_par_entree_eur} euros
                </div>
              </div>
            </Grid>
            <Grid item xs={8}>
              <div className="graphcard">
                Nombre d'entrées
                {/* a voir si c'est utile
                <iframe
                  src="https://data.culture.gouv.fr/chart/embed/?%3D&static=false&datasetcard=false"
                  width="400"
                  height="300"
                  frameborder="0"
                ></iframe>*/}
              </div>
            </Grid>
            <Grid item xs={4}>
              <div className="graphcard">
                Recette
                <Bar className="bar" data={data} />
                <div>
                  {this.state.info.fields.recette_guichet_meur_courants}{" "}
                  millions d'euros
                </div>
              </div>
            </Grid>
            <Grid item xs={7}>
              <div className="graphcard">Genre/Age des visiteurs</div>
            </Grid>
            <Grid item xs={5}>
              <div className="graphcard">
                {/*Géolocalisation des cinémas en France
                <iframe
                  src="https://data.culture.gouv.fr/explore/embed/dataset/etablissements-cinematographiques/analyze/?%3D%3D&static=false&datasetcard=false"
                  width="400"
                  height="300"
                  frameborder="0"
                ></iframe>*/}
              </div>
            </Grid>
          </Grid>
        </Box>
      </Box>
    );
  }
}

export default App;
